                    .OrderBy(sort + " " + sortdir)
                    .Skip((page - 1) * pageSize)
                    .Take(pageSize)
                    .ToList();

                records.TotalRecords = harvestingDifficultyData
                    .Count(x => x.fldOptConfDesc.ToUpper().Contains(filter.ToUpper()) ||
                                x.fldOptConfValue.ToUpper().Contains(filter.ToUpper()));
