                    .Count(x => x.fld_WlyhName.ToUpper().Contains(filter.ToUpper()));
            }

            else
            {
                records.Content = unitData.OrderBy(sort + " " + sortdir)
                    .Skip((page - 1) * pageSize)
                    .Take(pageSize)
                    .ToList();

                records.TotalRecords = unitData
                    .Count();
            }

            records.CurrentPage = page;
            records.PageSize = pageSize;
